import React, {Component} from "react";
import ReactVote from "react-vote";
import voteAPI from "../../utils/voteAPI";

class Vote extends Component {
    state = {
        data:{},
        userId:"1"
    };

    data = "";

    updateVote = (data) => {
        voteAPI.updateVote(this.data._id,  data).then(res =>{
            console.log(res.data);
        });
    };

    makeVotes = () => {
        voteAPI.getVotes().then(res =>  res.data.map(vote =>
            <ReactVote
                // styles={customStyle}
                // text={customText}
                data = {vote.data}
                onCreate={this.onCreate}
                onUpvote={this.onUpvote}
                onClose={this.onClose}
                onReset={this.onReset}
                onDownvote={this.onDownvote}
                onExpand={this.onExpand}
                onEdit={this.onEdit}
                isAdmin={true}
                clientId={"1"}
            /> ));
    };

    onCreate = (data) =>  {
        voteAPI.saveVote(data).then(res => {
            this.data = res.data;
            console.log(this.data);
        });
    };

    onUpvote = (data) => {
        this.updateVote(data);
    };

    onDownvote = (data) => {
        this.updateVote(data);
    };  
    
    onClose = (data) => {
        this.updateVote(data);
    };

    onReset = (data) => {
        this.updateVote(data);
    };

    onExpand = (data) => {
        this.updateVote(data);
    };

    onEdit = (data) => {
        this.updateVote(data);
    };

    render() {
        return (
        <div>
            <ReactVote
            // styles={customStyle}
            // text={customText}
            onCreate={this.onCreate}
            onUpvote={this.onUpvote}
            onClose={this.onClose}
            onReset={this.onReset}
            onDownvote={this.onDownvote}
            onExpand={this.onExpand}
            onEdit={this.onEdit}
            isAdmin={true}
            clientId={"1"}
            />
            {this.makeVotes()}
        </div>
        );
    };
};

export default Vote;
